import React, { useState } from 'react';
import {useParams} from "react-router-dom"
function CreateTeams() {
  const {tournamentId}=useParams();
  const [teamName, setTeamName] = useState('');

  const handleSubmit = async () => {
    console.log('Tournament ID:',tournamentId);
    console.log('Registering team with data:', {teamName});
    if (!tournamentId|| !teamName){
      alert('Tournament ID or Team Name is missing!');
      return;
    }
    try{
      const response = await fetch('http://localhost:5000/create-teams', { 
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({tournamentId,teamName }),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Response from server:', data);
        alert('Team registered successfully!');
      } else {
        console.error('Error from server:', data.error);
        alert(`Failed to register team: ${data.error}`);
      }
    }catch(error){
      console.error('Fetch Error:', error);
      alert('An unexpected erroroccured while registering the team');
    }
  };

  return (
    <div className='register-team'>
      <h2>Register Team</h2>
      <input 
        type="text" 
        placeholder="Team Name" 
        value={teamName} 
        onChange={(e) => setTeamName(e.target.value)} 
      />
      <button onClick={handleSubmit}>Register</button>
    </div>
  );
}

export default CreateTeams;
