import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000"); // WebSocket connection

const LiveMatch = () => {
  const { matchId } = useParams();
  console.log("Match ID:", matchId);

  const [match, setMatch] = useState(null);
  const [timer, setTimer] = useState(0);
  const [running, setRunning] = useState(false);
  const [timeoutsA, setTimeoutsA] = useState(0);
  const [timeoutsB, setTimeoutsB] = useState(0);

  useEffect(() => {
    const fetchMatch = async () => {
      try {
        console.log(`Fetching match data for ID: ${matchId}`);
        const response = await fetch(`http://localhost:5000/api/matches/${matchId}`);
        if (!response.ok) {
          throw new Error("Failed to fetch match data");
        }
        const data = await response.json();
        console.log("Match data received:", data);

        // Ensure default values if fields are missing
        setMatch({
          home_team: data.home_team || "Team A",
          away_team: data.away_team || "Team B",
          scoreA: data.scoreA || 0,
          scoreB: data.scoreB || 0,
        });

        setTimeoutsA(data.timeoutsA ?? 3); // Default to 3 timeouts
        setTimeoutsB(data.timeoutsB ?? 3);
      } catch (err) {
        console.error("Error fetching match data:", err);
      }
    };

    fetchMatch();
  }, [matchId]);

  useEffect(() => {
    socket.on("matchUpdated", (updatedMatch) => {
      console.log("Received WebSocket update:", updatedMatch);
      if (updatedMatch.id === matchId) { // Ensure correct match ID comparison
        setMatch(updatedMatch);
      }
    });

    return () => socket.off("matchUpdated");
  }, [matchId]);

  const updateScore = async (team, points) => {
    if (!match) return;

    const newScoreA = team === "A" ? match.scoreA + points : match.scoreA;
    const newScoreB = team === "B" ? match.scoreB + points : match.scoreB;

    try {
      const response = await fetch(`http://localhost:5000/api/matches/${matchId}/score`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scoreA: newScoreA, scoreB: newScoreB }),
      });

      if (response.ok) {
        setMatch((prevMatch) => ({
          ...prevMatch,
          scoreA: newScoreA,
          scoreB: newScoreB,
        }));
        socket.emit("updateScore", { matchId, scoreA: newScoreA, scoreB: newScoreB });
      }
    } catch (err) {
      console.error("Error updating score:", err);
    }
  };

  const startTimer = (duration) => {
    if (!duration) {
      console.error("Invalid match duration");
      return;
    }
    setTimer(duration * 60);
    setRunning(true);
  };

  useEffect(() => {
    let interval;
    if (running && timer > 0) {
      interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
    } else {
      setRunning(false);
    }
    return () => clearInterval(interval);
  }, [running, timer]);

  const handleTimeout = async (team) => {
    if (!match) return;

    if (team === "A" && timeoutsA > 0) {
      setTimeoutsA((prev) => prev - 1);
      socket.emit("updateTimeout", { matchId, team: "A", timeoutsA: timeoutsA - 1 });
    } else if (team === "B" && timeoutsB > 0) {
      setTimeoutsB((prev) => prev - 1);
      socket.emit("updateTimeout", { matchId, team: "B", timeoutsB: timeoutsB - 1 });
    }
  };

  if (!match) return <p>Loading match data...</p>;

  return (
    <div>
      <h1>Live Match: {match.home_team} vs {match.away_team}</h1>
      <p>Score: {match.scoreA} - {match.scoreB}</p>
      <p>Timeouts - {match.home_team}: {timeoutsA} | {match.away_team}: {timeoutsB}</p>
      
      <button onClick={() => updateScore("A", 2)}>+2 {match.home_team}</button>
      <button onClick={() => updateScore("B", 2)}>+2 {match.away_team}</button>
      <button onClick={() => handleTimeout("A")}>Timeout {match.home_team}</button>
      <button onClick={() => handleTimeout("B")}>Timeout {match.away_team}</button>
      
      <div>
        <p>Timer: {Math.floor(timer / 60)}:{timer % 60 < 10 ? `0${timer % 60}` : timer % 60}</p>
        <button onClick={() => startTimer(10)}>Start 10 min Timer</button>
      </div>
    </div>
  );
};

export default LiveMatch;
