import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";
import "../styles/Match.css";

const Match = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [scoreA, setScoreA] = useState(0);
  const [scoreB, setScoreB] = useState(0);
  const [players, setPlayers] = useState([]);
  const [fouls, setFouls] = useState({});
  const [mainTimer, setMainTimer] = useState(600);
  const [secondTimer, setSecondTimer] = useState(120);

  const socket = io("http://localhost:5000", {
    transports: ["websocket", "polling"],
    reconnection: true,
    reconnectionAttempts: 10,
    reconnectionDelay: 2000,
  });

  useEffect(() => {
    const fetchMatch = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/matches/${matchId}`);
        if (!response.ok) throw new Error("Failed to fetch match data");

        const data = await response.json();
        if (!data.match) throw new Error("Match not found");

        setMatch(data.match);
        setScoreA(data.match.scoreA || 0);
        setScoreB(data.match.scoreB || 0);
        setPlayers(data.players || []);
        setFouls(data.players.reduce((acc, player) => ({ ...acc, [player.id]: player.fouls || 0 }), {}));
      } catch (err) {
        console.error("Error fetching match:", err);
        setMatch(null);
      } finally {
        setLoading(false);
      }
    };

    fetchMatch();

    socket.on("updateMatch", (updatedMatch) => {
      if (updatedMatch.matchId === matchId) {
        setScoreA(updatedMatch.scoreA || 0);
        setScoreB(updatedMatch.scoreB || 0);
        setPlayers(updatedMatch.players || []);
        setFouls(updatedMatch.players.reduce((acc, player) => ({ ...acc, [player.id]: player.fouls || 0 }), {}));
      }
    });

    return () => {
      socket.off("updateMatch");
      socket.disconnect();
    };
  }, [matchId]);

  if (loading) return <p>Loading match data...</p>;
  if (!match) return <p>No match found!</p>;

  return (
    <div className="match-container">
      <h1>{match.home_team} vs {match.away_team}</h1>
      <h2>Match Score</h2>
      <p>Score: {scoreA} - {scoreB}</p>
      <p>Main Timer: {Math.floor(mainTimer / 60)}:{(mainTimer % 60).toString().padStart(2, "0")}</p>
      <p>Second Timer: {Math.floor(secondTimer / 60)}:{(secondTimer % 60).toString().padStart(2, "0")}</p>
      <div>
        <h3>Players</h3>
        {players.map((player) => (
          <p key={player.id}>{player.name} - {player.points} Points | Fouls: {fouls[player.id]}</p>
        ))}
      </div>
    </div>
  );
};

export default Match;
