import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [tournament, setTournament] = useState(null);
    const [matches, setMatches] = useState([]);
    const [playoffMatches, setPlayoffMatches] = useState([]);
    const [teams, setTeams] = useState([]);
    const [pointsTable, setPointsTable] = useState([]);
    const [tournamentWinner, setTournamentWinner] = useState(null);
    const [playoffsScheduled, setPlayoffsScheduled] = useState(false);

    useEffect(() => {
        async function fetchData() {
            await fetchTournamentDetails();
            await fetchMatches();
            await fetchTeams();
            await fetchPointsTable();
            await fetchPlayoffMatches();
        }
        fetchData();
    }, [id]);

    const fetchTournamentDetails = async () => {
        try {
            const res = await fetch(`http://localhost:5000/tournament/${id}`);
            const data = await res.json();
            if (data && data.id) {
                setTournament(data);
            }
        } catch (error) {
            console.error('Error fetching tournament details:', error);
        }
    };

    const fetchMatches = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
            const data = await res.json();
            setMatches(data);
        } catch (error) {
            console.error('Error fetching matches:', error);
        }
    };

    const fetchTeams = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/teams`);
            const data = await res.json();
            setTeams(data);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    const fetchPointsTable = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
            const data = await res.json();
            setPointsTable(data);
        } catch (error) {
            console.error('Error fetching points table:', error);
        }
    };

    const fetchPlayoffMatches = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
            const data = await res.json();
            const playoffs = data.filter(match => match.round.includes("Semifinal") || match.round.includes("Final"));
            setPlayoffMatches(playoffs);
            setPlayoffsScheduled(playoffs.length > 0);
        } catch (error) {
            console.error('Error fetching playoff matches:', error);
        }
    };

    if (!tournament) return <p>Loading tournament details...</p>;

    return (
        <div className="container1">
            <nav className="navbar">
                <img src="/logo.png" alt="Logo" className='tour-logo'/>
                <ul className="nav-links">
                    <li>Home</li>
                    <li>Game</li>
                    <li>Tournaments</li>
                    <li>About Us</li>
                    <li>Contact</li>
                    <li>Account</li>
                </ul>
            </nav>

            <div className='tournament'>
                <h2>{tournament.name} ({tournament.type})</h2>

                <p className='section-title'>Teams: {teams.length}</p>
                {teams.length > 0 ? (
                    <div>
                        {teams.map((team, index) => (
                            <span key={index} className='team-tag'>{team.teamName}</span>
                        ))}
                    </div>
                ) : <p>No teams available.</p>}

                <p className='section-title'>Matches</p>
                {matches.length === 0 ? <p>No matches scheduled yet.</p> : (
                    <div>
                        {matches.map((match, index) => (
                            <div key={index} className="match-card">
                                <h3>{match.home_team} vs {match.away_team}</h3>
                                <p>Date: {new Date(match.time).toLocaleString()}</p>
                                {match.status === 'completed' ? (
                                    <p className='match-result'>
                                        üèÜ Winner: {match.winner} <br />
                                        Final Score: {match.home_team} {match.teamA_score} - {match.away_team} {match.teamB_score}
                                    </p>
                                ) : (
                                    <div className='buttons'>
                                        <button onClick={() => navigate(`/live-match/${match.id}`)} className="start-btn">Start</button>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                )}

                <h2>Points Table</h2>
                <table border="1">
                    <thead>
                        <tr>
                            <th>Team</th>
                            <th>Played</th>
                            <th>Wins</th>
                            <th>Losses</th>
                            <th>Points</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pointsTable.length > 0 ? (
                            pointsTable.map((team, index) => (
                                <tr key={index}>
                                    <td>{team.team_name}</td>
                                    <td>{team.games_played}</td>
                                    <td>{team.wins}</td>
                                    <td>{team.losses}</td>
                                    <td>{team.points}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="5">No points data available</td>
                            </tr>
                        )}
                    </tbody>
                </table>

                {/* ‚úÖ SHOW PLAYOFFS ONLY IF SCHEDULED */}
                {playoffsScheduled && (
                    <>
                        <h2>Playoffs</h2>
                        {playoffMatches.map((match, index) => (
                            <div key={index} className="match-card">
                                <h3>{match.home_team} vs {match.away_team}</h3>
                                <p>Round: {match.round}</p>
                                <p>Date: {new Date(match.time).toLocaleString()}</p>
                                <p>Status: {match.status}</p>
                                <button onClick={() => navigate(`/live-match/${match.id}`)}>Start Match</button>
                            </div>
                        ))}
                    </>
                )}

                {/* ‚úÖ RESTORE "GO TO PLAYOFFS" BUTTON */}
                <button onClick={() => navigate(`/playoffs/${id}`)}>Go to Playoffs</button>

                <h3>Tournament Winner</h3>
                {tournamentWinner && <h3>Tournament Winner: {tournamentWinner}</h3>}

                <button onClick={() => navigate(-1)} className="back-btn">Back</button>
            </div>
        </div>
    );
}

export default ViewTournament;
