import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "../styles/ViewTournament.css";

function Tournament() {
  const { id } = useParams(); // Get tournament ID from URL
  const navigate = useNavigate();
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/tournament/${id}/matches`)
      .then(response => response.json())
      .then(data => {
        console.log("Fetched matches:", data); // Debugging output
        if (Array.isArray(data)) { // Check if data is an array
          setMatches(data);  // Directly set the matches array
        } else if (data && data.matches) {
          setMatches(data.matches); // In case API returns { matches: [...] }
        } else {
          console.error("Unexpected API response format:", data);
        }
      })
      .catch(error => console.error('Error fetching matches:', error));
  }, [id]);
  

  return (
    <div className="container1">
      <nav className="navbar">
        <img src="/logo.png" alt="Logo" className='tour-logo' />
        <ul className="nav-links">
          <li>Home</li>
          <li>Game</li>
          <li>Tournaments</li>
          <li>About Us</li>
          <li>Contact</li>
          <li>Account</li>
        </ul>
      </nav>
      <div className='tournament-list'>
  <h2>Matches for Tournament {id}</h2>
  {matches.length > 0 ? (
    matches.map((match) => (
      <div key={match.id} className="tournament-card">
        <h3>{match.home_team} vs {match.away_team}</h3>
        <p>Date: {match.date || "TBD"}</p>
        <button onClick={() => navigate(`/live-match/${match.id}`)} className="view-btn">
          View Match
        </button>
      </div>
    ))
  ) : (
    <p>No matches available.</p>
  )}
</div>


    </div>
  );
}

export default Tournament;
