import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [tournament, setTournament] = useState(null);
  const [matches, setMatches] = useState([]);
  const [teams, setTeams] = useState([]);
  const [pointsTable, setPointsTable] = useState([]);
  const [tournamentWinner, setTournamentWinner] = useState(null);
  const [allMatchesCompleted, setAllMatchesCompleted] = useState(false);
  useEffect(() => {
    async function fetchData() {
      await fetchTournamentDetails();
      await fetchMatches();
      await fetchTeams();
      await fetchPointsTable();
    }
    fetchData();
  }, [id,matches]);
  useEffect(() => {
    fetch(`http://localhost:5000/api/tournament/${id}/points-table`)
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
        })
        .then(data => {
            if (Array.isArray(data)) {
                setPointsTable(data);
            } else {
                console.error("Unexpected response format:", data);
                setPointsTable([]); // Ensure it's an array
            }
        })
        .catch(error => {
            console.error('Error fetching points table:', error);
            setPointsTable([]); // Ensure it's always an array
        });
}, [id]);
useEffect(() => {
  fetch(`http://localhost:5000/api/tournament/${id}/matches`)
    .then(response => response.json())
    .then(data => setMatches(Array.isArray(data) ? data : []))
    .catch(error => console.error('Error fetching matches:', error));
}, [id]);
useEffect(() => {
    fetch(`http://localhost:5000/api/tournament/${id}`)
        .then(response => response.json())
        .then(data => setTournamentWinner(data.winner))
        .catch(error => console.error('Error fetching tournament winner:', error));
}, [id]);
useEffect(() => {
  fetch(`http://localhost:5000/api/tournament/${id}/playoff-matches`)
    .then(response => response.json())
    .then(data => setPlayoffMatches(Array.isArray(data) ? data : []))
    .catch(error => console.error('Error fetching playoff matches:', error));
}, [id]);
useEffect(() => {
  async function checkMatchesCompletion() {
      const response = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/matches`);
      const matches = await response.json();
      setAllMatchesCompleted(matches.every(match => match.status === "completed"));
  }
  checkMatchesCompletion();
}, [tournamentId]);

const handleStartPlayoffs = async () => {
  try {
      const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
      const data = await res.json();

      if (Array.isArray(data) && data.length >= 4) {
          const topTeams = data.sort((a, b) => b.points - a.points).slice(0, 4);
          
          // Send request to backend to schedule playoff matches
          await fetch(`http://localhost:5000/api/tournament/${id}/schedule-playoffs`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ teams: topTeams })
          });

          navigate(`/playoffs/${id}`); // ‚úÖ Navigate to Playoffs page
      } else {
          alert("Not enough teams to start playoffs!");
      }
  } catch (error) {
      console.error("Error starting playoffs:", error);
  }
};
const fetchPointsTable = async () => {
  try {
    const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
    const data = await res.json();
    if (Array.isArray(data)) {
      // Sorting teams in descending order based on points
      const sortedData = data.sort((a, b) => b.points - a.points);
      setPointsTable(sortedData);
    } else {
      console.error("Unexpected API response format:", data);
    }
  } catch (error) {
    console.error('Error fetching points table:', error);
  }
};
  const fetchTournamentDetails = async () => {
    try {
      const res = await fetch(`http://localhost:5000/tournament/${id}`);
      const data = await res.json();
      if (data && data.id) {
        setTournament(data);
      } else {
        console.error("Tournament not found.");
      }
    } catch (error) {
      console.error('Error fetching tournament details:', error);
    }
  };
  const checkIfAllMatchesCompleted = (matches) => {
    if (matches.length > 0 && matches.every(match => match.status === 'completed')) {
      setAllMatchesCompleted(true);
    }
  };

  const fetchMatches = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
      const data = await res.json();
      setMatches(data);
      checkIfAllMatchesCompleted(data);
    } catch (error) {
      console.error('Error fetching matches:', error);
    }
  };

  const fetchTeams = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/tournament/${id}/teams`);
      const data = await res.json();
      if (Array.isArray(data) && data.length > 0) {
        setTeams(data);
      } else {
        console.error("Unexpected API response format or no teams available:", data);
      }
    } catch (error) {
      console.error('Error fetching teams:', error);
    }
  };

  const handleDeleteMatch = async (matchId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/matches/${matchId}`, { method: 'DELETE' });
      if (res.ok) {
        setMatches(matches.filter(match => match.id !== matchId));
        alert("Match deleted successfully!"); 
      } else {
        console.error("Failed to delete match");
      }
    } catch (error) {
      console.error('Error deleting match:', error);
    }
  };

  const handleStartMatch = async (matchId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/matches/${matchId}/start`, { method: 'POST' });
      if (res.ok) {
        alert("Match started successfully!"); 
        navigate(`/live-match/${matchId}`); // ‚úÖ Navigate to live match page
      } else {
        console.error("Failed to start match");
      }
    } catch (error) {
      console.error('Error starting match:', error);
    }
  };

  if (!tournament) return <p>Loading tournament details...</p>;

  return (
    <div className="container1">
      <nav className="navbar">
          <img src="/logo.png" alt="Logo" className='tour-logo'/>
        <ul className="nav-links">
          <li>Home</li>
          <li>Game</li>
          <li>Tournaments</li>
          <li>About Us</li>
          <li>Contact</li>
          <li>Account</li>
        </ul>
      </nav>
      <div className='tournament'>
      <h2>{tournament.name} ({tournament.type})</h2>
      <div className='spacer'></div>
      <div className='teams'>
      <p className='section-title'>Teams: {teams.length}</p>
      {teams.length > 0 ? (
        <div>
          {teams.map((team, index) => (
            <span key={index} className='team-tag'>
              {team.teamName}
            </span>
          ))}
          </div>
      ) : (
        <p>No teams available.</p>
      )}
      </div>

      <p className='section-title'>Matches</p>
      <div className='matches'>
      {matches.length === 0 ? (
        <p>No matches scheduled yet.</p>
      ) : (<div>
          {matches.map((match, index) => (
            <div key={index} className="match-card">
              <h3>{match.home_team} vs {match.away_team}</h3>
              <p>Date: {new Date(match.time).toLocaleString()}</p>
              {match.status==='completed'?(
                <p className='match-result'>
                  üèÜ Winner: {match.winner} <br />
                  Final Score: {match.home_team} {match.teamA_score} - {match.away_team} {match.teamB_score}
                </p>
              ):(<div className='buttons'>
                <button onClick={() => handleStartMatch(match.id)} className="start-btn">Start</button>
                <button onClick={() => handleDeleteMatch(match.id)} className="delete-btn">Delete</button>
                </div>
              )}
            </div>
          ))}
          </div>
      )}
    </div>
    <h2>Points Table</h2>
    <table border="1">
      <thead><tr><th>Team</th><th>Played</th><th>Wins</th><th>Losses</th><th>Points</th></tr></thead>
      <tbody>
      {pointsTable.length > 0 ? (
    pointsTable.map((team, index) => (
        <tr key={index}>
            <td>{team.team_name}</td>
            <td>{team.games_played}</td>
            <td>{team.wins}</td>
            <td>{team.losses}</td>
            <td>{(team.wins*1)}</td>
        </tr>
    ))
) : (
    <tr>
        <td colSpan="5">No points data available</td>
    </tr>
)}
</tbody>
</table>
<h2>Playoff Matches</h2>
<button
        onClick={() => navigate(`/playoffs/${id}`)}
        disabled={!allMatchesCompleted}
    >
        Go to Playoffs
    </button>
<h3>Tournament winner</h3>
  {tournamentWinner && <h3>Tournament Winner: {tournamentWinner}</h3>}
    <button onClick={() => navigate(-1)} className="back-btn">Back</button>
    </div>
    </div>
  );
}

export default ViewTournament;
