import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ViewerDashboard.css';

function ViewerDashboard() {
  const [matches, setMatches] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch scheduled matches from the backend
    fetch('http://localhost:5000/api/tournaments/scheduled-matches')
      .then((res) => res.json())
      .then((data) => {
        setMatches(data);
      })
      .catch((error) => console.error('Error fetching matches:', error));
  }, []);

  // Navigate to live matches page
  const handleViewLiveMatches = () => {
    navigate('/live-match');
  };

  // Navigate to completed matches page
  const handleViewCompletedMatches = () => {
    navigate('/completed-match');
  };

  return (
    <div className="viewer">
      <h2>Viewer Dashboard</h2>
      <div>
        <button onClick={handleViewLiveMatches}>View Live Matches</button>
        </div><div>
        <button onClick={handleViewCompletedMatches}>View Completed Matches</button>
      </div>

      <div>
        <h2>Upcoming Matches</h2>
        {/* Display message if no matches are scheduled */}
        {matches.length === 0 ? (
          <p>No matches are scheduled.</p>
        ) : (
          <ul>
            {matches.map((match, index) => {
              const matchDateTime = new Date(match.time);
              return (
                <li key={index}>
                  {match.home_team} vs {match.away_team} on {matchDateTime.toLocaleDateString()} at {matchDateTime.toLocaleTimeString()}
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
}

export default ViewerDashboard;
