import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const Match = () => {
    const { matchId } = useParams();
    const [matchData, setMatchData] = useState(null);
    const [teamScores, setTeamScores] = useState({ t1: 0, t2: 0 });
    const [playerScores, setPlayerScores] = useState([]);

    // Fetch Match Data from API
    const fetchMatchData = async () => {
        try {
            console.log(`Fetching data for match ID: ${matchId}`);
            const response = await fetch(`/api/matches/${matchId}`);
            if (!response.ok) throw new Error("Failed to fetch match data");

            const data = await response.json();
            console.log("Match data received:", data);

            if (!data.team1Name || !data.team2Name) {
                console.error("Error: Team names missing in API response!");
            }

            setMatchData(data);
            setTeamScores({ t1: data.team1Score || 0, t2: data.team2Score || 0 });
            setPlayerScores(data.players || []);
        } catch (error) {
            console.error("Error fetching match data:", error);
        }
    };

    useEffect(() => {
        fetchMatchData();

        // Listen for live updates via WebSocket
        socket.on("matchUpdate", (data) => {
            console.log("WebSocket Update:", data);
            if (!data.team1Name || !data.team2Name) {
                console.error("WebSocket error: Missing team names!");
            }
            setMatchData(data);
            setTeamScores({ t1: data.team1Score || 0, t2: data.team2Score || 0 });
            setPlayerScores(data.players || []);
        });

        return () => {
            socket.off("matchUpdate");
        };
    }, [matchId]);

    if (!matchData) {
        return <h2>Loading Match Data...</h2>;
    }

    return (
        <div>
            <h2>
                {matchData.team1Name || "Team 1"} vs {matchData.team2Name || "Team 2"}
            </h2>
            <h3>Match Score</h3>
            <p>{teamScores.t1} - {teamScores.t2}</p>

            <h3>Players</h3>
            {playerScores.length === 0 ? (
                <p>No player data available</p>
            ) : (
                <ul>
                    {playerScores.map((player) => (
                        <li key={player.id}>
                            {player.name}: {player.score} points
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Match;
