import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function Playoffs() {
    const { tournamentId } = useParams();
    const navigate = useNavigate();
    const [matches, setMatches] = useState([]);
    const [results, setResults] = useState({});

    useEffect(() => {
        async function fetchMatches() {
            try {
                const response = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/matches`);
                const data = await response.json();
                setMatches(data.filter(match => match.round.includes('Semifinal')));
            } catch (error) {
                console.error("Error fetching playoff matches:", error);
            }
        }
        fetchMatches();
    }, [tournamentId]);

    const recordResult = async (matchId, winner) => {
        try {
            await fetch(`http://localhost:5000/api/matches/${matchId}/record-winner`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ winner })
            });

            setResults(prev => ({ ...prev, [matchId]: winner }));

            if (Object.keys(results).length === 2) {
                scheduleNextRound();
            }
        } catch (error) {
            console.error("Error updating match result:", error);
        }
    };

    const scheduleNextRound = async () => {
        const match1Winner = results[matches[0].id];
        const match1Loser = matches[0].home_team === match1Winner ? matches[0].away_team : matches[0].home_team;
        const match2Winner = results[matches[1].id];

        const nextMatches = [
            { home: match1Loser, away: match2Winner, round: 'Elimination Final' },
            { home: match1Winner, away: null, round: 'Grand Final' } // Await Elimination Final result
        ];

        try {
            for (const match of nextMatches) {
                await fetch(`http://localhost:5000/api/tournament/${tournamentId}/schedule-match`, {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(match)
                });
            }
            alert("Next playoff rounds scheduled!");
        } catch (error) {
            console.error("Error scheduling next round:", error);
        }
    };

    return (
        <div className="container">
            <h2>Playoff Matches</h2>
            {matches.map(match => (
                <div key={match.id} className="match-card">
                    <h3>{match.home_team} vs {match.away_team}</h3>
                    <p>Round: {match.round}</p>
                    <p>Date: {new Date(match.time).toLocaleString()}</p>
                    <button onClick={() => recordResult(match.id, match.home_team)}>Win {match.home_team}</button>
                    <button onClick={() => recordResult(match.id, match.away_team)}>Win {match.away_team}</button>
                </div>
            ))}
            <button onClick={() => navigate(-1)}>Back</button>
        </div>
    );
}

export default Playoffs;
