import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [tournament, setTournament] = useState(null);
    const [matches, setMatches] = useState([]);
    const [playoffMatches, setPlayoffMatches] = useState([]);
    const [teams, setTeams] = useState([]);
    const [pointsTable, setPointsTable] = useState([]);

    useEffect(() => {
        async function fetchData() {
            await fetchTournamentDetails();
            await fetchMatches();
            await fetchTeams();
            await fetchPointsTable();
        }
        fetchData();
    }, [id]);

    const fetchTournamentDetails = async () => {
        try {
            const res = await fetch(`http://localhost:5000/tournament/${id}`);
            const data = await res.json();
            if (data?.id) {
                setTournament(data);
            }
        } catch (error) {
            console.error('Error fetching tournament details:', error);
        }
    };

    const fetchMatches = async () => {
      try {
          const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
          const data = await res.json();
          if (!Array.isArray(data)) return;
  
          setMatches(data);
          data.forEach(match => console.log("Match Round:", match.round, typeof match.round));

          // ✅ Ensure match.round exists before using .includes()
          setPlayoffMatches(data.filter(match => 
            match.round && String(match.round).includes("Semifinal") || String(match.round).includes("Final")
        ));
        
      } catch (error) {
          console.error('Error fetching matches:', error);
      }
  };
  

    const fetchTeams = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/teams`);
            const data = await res.json();
            if (Array.isArray(data)) setTeams(data);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    const fetchPointsTable = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
            const data = await res.json();
            if (!Array.isArray(data)) return;

            setPointsTable(data.map(team => ({
                ...team,
                points: team.wins, // Ensure points are correct
            })));
        } catch (error) {
            console.error('Error fetching points table:', error);
        }
    };

    const deleteMatch = async (matchId) => {
        try {
            await fetch(`http://localhost:5000/api/match/${matchId}`, { method: 'DELETE' });
            fetchMatches();
        } catch (error) {
            console.error('Error deleting match:', error);
        }
    };

    if (!tournament) return <p>Loading tournament details...</p>;

    return (
        <div className="container1">
            <nav className="navbar">
                <img src="/logo.png" alt="Logo" className='tour-logo'/>
                <ul className="nav-links">
                    <li>Home</li>
                    <li>Game</li>
                    <li>Tournaments</li>
                    <li>About Us</li>
                    <li>Contact</li>
                    <li>Account</li>
                </ul>
            </nav>

            <div className='tournament'>
                <h2>{tournament.name} ({tournament.type})</h2>

                <p className='section-title'>Scheduled Matches</p>
                {matches.length === 0 ? <p>No matches scheduled yet.</p> : (
                    <div>
                        {matches.filter(match => !match.round.includes("Semifinal") && !match.round.includes("Final")).map((match, index) => (
                            <div key={index} className="match-card">
                                <h3>{match.home_team} vs {match.away_team}</h3>
                                <p>Date: {new Date(match.time).toLocaleString()}</p>
                                {match.status === 'completed' ? (
                                    <p className='match-result'>
                                        🏆 Winner: {match.winner} <br />
                                        

                                    </p>
                                ) : (
                                    <div className='buttons'>
                                        <button onClick={() => navigate(`/live-match/${match.id}`)} className="start-btn">Start</button>
                                        <button onClick={() => deleteMatch(match.id)} className="delete-btn">Delete</button>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                )}

                {playoffMatches.length > 0 && (
                    <>
                        <h2>Playoffs</h2>
                        {playoffMatches.map((match, index) => (
                            <div key={index} className="match-card">
                                <h3>{match.home_team} vs {match.away_team}</h3>
                                <p>Round: {match.round}</p>
                                <p>Date: {new Date(match.time).toLocaleString()}</p>
                                <p>Status: {match.status}</p>
                                <div className='buttons'>
                                    <button onClick={() => navigate(`/live-match/${match.id}`)} className="start-btn">Start</button>
                                    <button onClick={() => deleteMatch(match.id)} className="delete-btn">Delete</button>
                                </div>
                            </div>
                        ))}
                    </>
                )}

                <h2>Points Table</h2>
                <table border="1">
                    <thead>
                        <tr>
                            <th>Team</th>
                            <th>Played</th>
                            <th>Wins</th>
                            <th>Losses</th>
                            <th>Points</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pointsTable.length > 0 ? (
                            pointsTable.map((team, index) => (
                                <tr key={index}>
                                    <td>{team.team_name}</td>
                                    <td>{team.games_played}</td>
                                    <td>{team.wins}</td>
                                    <td>{team.losses}</td>
                                    <td>{team.points}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="5">No points data available</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ViewTournament;
