import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { io } from "socket.io-client";
import "../styles/LiveMatch.css";

const socket = io("http://localhost:5000", { transports: ["websocket", "polling"] });

const Match = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [scoreA, setScoreA] = useState(0);
  const [scoreB, setScoreB] = useState(0);
  const [players, setPlayers] = useState([]);
  const [fouls, setFouls] = useState({});
  const [mainTimer, setMainTimer] = useState(600);
  const [secondTimer, setSecondTimer] = useState(120);

  useEffect(() => {
    const fetchMatch = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/matches/${matchId}`);
        if (!response.ok) throw new Error("Failed to fetch match data");
        const data = await response.json();

        setMatch(data.match);
        setScoreA(data.match.scoreA || 0);
        setScoreB(data.match.scoreB || 0);
        setPlayers(data.players.map(player => ({ ...player, score: 0 })) || []);
        setFouls(
          data.players.reduce((acc, player) => ({ ...acc, [player.id]: player.fouls || 0 }), {})
        );
      } catch (err) {
        console.error("Error fetching match:", err);
        setMatch(null);
      } finally {
        setLoading(false);
      }
    };

    fetchMatch();
    
    // Listen for WebSocket updates
    socket.on("updateMatch", (updatedMatch) => {
      setMatch(updatedMatch);
      setScoreA(updatedMatch.scoreA); // Fix: Ensure live update
      setScoreB(updatedMatch.scoreB);
    });

    socket.on("updatePlayers", (updatedPlayers) => {
      console.log("Updated Players Received:", updatedPlayers);
      setPlayers([...updatedPlayers]); // Ensure state updates
    });
    
    
    socket.on("playerFoulUpdated", (data) =>
      setFouls((prev) => ({ ...prev, [data.playerId]: data.fouls }))
    );
    socket.on("updateMainTimer", (time) => setMainTimer(time));
    socket.on("updateSecondTimer", (time) => setSecondTimer(time));

    return () => {
      socket.off("updateMatch");
      socket.off("updatePlayers");
      socket.off("playerFoulUpdated");
      socket.off("updateMainTimer");
      socket.off("updateSecondTimer");
    };
  }, [matchId]);
  useEffect(() => {
    console.log("Players Fetched: ", players);
  }, [players]);
  
  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${String(min).padStart(2, "0")}:${String(sec).padStart(2, "0")}`;
  };

  if (loading) return <p>Loading match data...</p>;
  if (!match) return <p>No match found!</p>;

  return (
    <div className="scoreboard-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">
          <span className="logo-box">24</span>
          <span className="logo-text">Score It</span>
        </div>
        <ul>
          <li><Link to="/organizer">Home</Link></li>
          <li><Link to="/game">Game</Link></li>
          <li><Link to={`/view-tournament/${matchId}`}>Tournaments</Link></li>
          <li><Link to="/about">About Us</Link></li>
          <li><Link to="/contact">Contact</Link></li>
          <li><Link to="/account">Account</Link></li>
        </ul>
      </nav>

      {/* Match Score */}
      <h1 className="match-title">{match.home_team} vs {match.away_team}</h1>
      <h2 className="match-score">Match Score</h2>

      {/* Timers */}
      <div className="timer-section">
        <div className="timer-box">
          <div className="time-display">{formatTime(mainTimer)}</div>
        </div>
        <div className="timer-box">
          <div className="time-display">{formatTime(secondTimer)}</div>
        </div>
      </div>

      {/* Team Score and Players */}
      <div className="team-section">
        {["home_team", "away_team"].map((teamType, index) => (
          <div key={index} className="team-box">
            <h3>{teamType === "home_team" ? match.home_team : match.away_team}</h3>

            {/* FIX: Show Updated Score */}
            <div className="score">{teamType === "home_team" ? scoreA : scoreB}</div>

            {/* Fouls Section */}
            <div className="fouls">
              <p>Fouls:</p>
              <div className="foul-box">
                {players
                  .filter((p) => p.teamId === (teamType === "home_team" ? match.home_team_id : match.away_team_id))
                  .reduce((sum, p) => sum + (fouls[p.id] || 0), 0)}
              </div>
            </div>

            {/* Players List */}
            <h4>Players:</h4>
<ul className="player-list">
  {players
    .filter((player) =>
      teamType === "home_team"
        ? player.teamName === match.home_team  // Fix: Use teamName
        : player.teamName === match.away_team
    )
    .map((player) => (
      <li key={player.id} className="player-row">
        <span className="player-name">{player.playerName}</span>
        <span className="score-box">{player.score}</span> {/* Live Player Score */}
      </li>
    ))}
</ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Match;
