import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [tournament, setTournament] = useState(null);
    const [matches, setMatches] = useState([]);
    const [playoffMatches, setPlayoffMatches] = useState([]);
    const [pointsTable, setPointsTable] = useState([]);
    const [playoffStage, setPlayoffStage] = useState(0);
    const [allMatchesCompleted, setAllMatchesCompleted] = useState(false);

    useEffect(() => {
        async function fetchData() {
            await fetchTournamentDetails();
            await fetchMatches();
            await fetchPointsTable();
            await fetchPlayoffStage();
        }
        fetchData();
    }, [id]);

    const fetchTournamentDetails = async () => {
        try {
            const res = await fetch(`http://localhost:5000/tournament/${id}`);
            const data = await res.json();
            if (data?.id) {
                setTournament(data);
            }
        } catch (error) {
            console.error('Error fetching tournament details:', error);
        }
    };

    const fetchMatches = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
            const data = await res.json();
            if (!Array.isArray(data)) return;
            
            setMatches(data);
            setPlayoffMatches(data.filter(match => match.round.includes("Semifinal") || match.round.includes("Final")));

            const allCompleted = data
                .filter(match => !match.round.includes("Semifinal") && !match.round.includes("Final"))
                .every(match => match.status === "completed");
            setAllMatchesCompleted(allCompleted);
        } catch (error) {
            console.error('Error fetching matches:', error);
        }
    };

    const fetchPointsTable = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
            const data = await res.json();
            if (!Array.isArray(data)) return;
            setPointsTable(data);
        } catch (error) {
            console.error('Error fetching points table:', error);
        }
    };

    const fetchPlayoffStage = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/playoff-stage`);
            const data = await res.json();
            setPlayoffStage(data.stage);
        } catch (error) {
            console.error('Error fetching playoff stage:', error);
        }
    };

    return (
        <div className="container1">
            <h2>{tournament?.name} ({tournament?.type})</h2>

            <h3>Scheduled Matches</h3>
            {matches.map((match, index) => (
                <div key={index} className="match-card">
                    <h3>{match.home_team} vs {match.away_team}</h3>
                    <p>Date: {new Date(match.time).toLocaleString()}</p>
                    <p>Status: {match.status}</p>
                    {match.status === 'completed' && (
                        <p className='match-result'>
                            🏆 Winner: {match.winner} <br />
                            <p>Score:</p>
                            {match.home_team} = {match.scoreA} - {match.scoreB} = {match.away_team}
                        </p>
                    )}
                </div>
            ))}

            <h2>Playoffs</h2>
            <button 
                className="playoff-btn" 
                onClick={() => navigate(`/playoffs/${id}`)}
                disabled={!allMatchesCompleted || playoffStage > 3}
            >
                Proceed to Playoffs
            </button>

            {playoffMatches.map((match, index) => (
                <div key={index} className="match-card">
                    <h3>{match.home_team} vs {match.away_team}</h3>
                    <p>Round: {match.round}</p>
                    <p>Status: {match.status}</p>
                    {match.status === 'completed' && (
                        <p className='match-result'>
                            🏆 Winner: {match.winner} <br />
                            <p>Score:</p>
                            {match.home_team} = {match.scoreA} - {match.scoreB} = {match.away_team}
                        </p>
                    )}
                </div>
            ))}

            <h3>Points Table</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Team</th>
                        <th>Played</th>
                        <th>Wins</th>
                        <th>Losses</th>
                        <th>Points</th>
                    </tr>
                </thead>
                <tbody>
                    {pointsTable.map((team, index) => (
                        <tr key={index}>
                            <td>{team.team_name}</td>
                            <td>{team.games_played}</td>
                            <td>{team.wins}</td>
                            <td>{team.losses}</td>
                            <td>{team.points}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default ViewTournament;
