import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [tournament, setTournament] = useState(null);
    const [matches, setMatches] = useState([]);
    const [playoffMatches, setPlayoffMatches] = useState([]);
    const [pointsTable, setPointsTable] = useState([]);
    const [playoffStage, setPlayoffStage] = useState(0);
    const [allMatchesCompleted, setAllMatchesCompleted] = useState(false);

    useEffect(() => {
        async function fetchData() {
            await fetchTournamentDetails();
            await fetchMatches();
            await fetchPointsTable();
            await fetchPlayoffStage();
        }
        fetchData();
    }, [id]);
    useEffect(() => {
        console.log("Fetched Playoff Matches:", data.map(match => ({
            id: match.id,
            home: match.home_team,
            away: match.away_team,
            round: match.round,  // ‚úÖ Check round
        })));
    }, [playoffMatches]);

    const fetchTournamentDetails = async () => {
        try {
            const res = await fetch(`http://localhost:5000/tournament/${id}`);
            const data = await res.json();
            if (data?.id) {
                setTournament(data);
            }
        } catch (error) {
            console.error('Error fetching tournament details:', error);
        }
    };

    const fetchMatches = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
            const data = await res.json();
            if (!Array.isArray(data)) return;
    
            const normalMatches = data.filter(match => 
                !match.round.includes("Semifinal") && !match.round.includes("Final")
            );
            const playoffMatches = data.filter(match => 
                match.round.includes("Semifinal") || match.round.includes("Final")
            );
    
            setMatches(normalMatches);
            setPlayoffMatches(playoffMatches);
    
            // ‚úÖ Check if all normal matches are completed
            const allNormalCompleted = normalMatches.every(match => match.status === "completed");
            setAllMatchesCompleted(allNormalCompleted);
    
            // ‚úÖ Check if all playoff matches are completed
            const allPlayoffsCompleted = playoffMatches.length > 0 && playoffMatches.every(match => match.status === "completed");
            setPlayoffStage(allPlayoffsCompleted ? 0 : 1);  // Disable button if not completed
        } catch (error) {
            console.error('Error fetching matches:', error);
        }
    };
    

    const fetchPointsTable = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
            const data = await res.json();
            console.log("Points Table Data:", data); // Debugging Log
            if (Array.isArray(data) && data.length > 0) {
                const sortedData = data.sort((a, b) => b.points - a.points || b.point_difference - a.point_difference);
                setPointsTable(sortedData);
            } else {
                setPointsTable([]); // Ensure an empty array if no data
            }
        } catch (error) {
            console.error('Error fetching points table:', error);
        }
    };
    

    const fetchPlayoffStage = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/playoff-stage`);
            const data = await res.json();
            setPlayoffStage(data.stage);
        } catch (error) {
            console.error('Error fetching playoff stage:', error);
        }
    };

    const deleteMatch = async (matchId) => {
        try {
            await fetch(`http://localhost:5000/api/matches/${matchId}`, { method: 'DELETE' });
            fetchMatches();
        } catch (error) {
            console.error('Error deleting match:', error);
        }
    };

    return (
        <div className="container1">
            <h2>{tournament?.name} ({tournament?.type})</h2>

            <h3>Scheduled Matches</h3>
{matches.length > 0 ? matches.map((match, index) => (
    <div key={index} className="match-card">
        <h3>{match.home_team} vs {match.away_team}</h3>
        <p>Date: {new Date(match.time).toLocaleString()}</p>
        <p>Status: {match.status}</p>
        {match.status === 'scheduled' && (
            <div className='buttons'>
                <button onClick={() => navigate(`/live-match/${match.id}`)} className="start-btn">Start</button>
                <button onClick={() => deleteMatch(match.id)} className="delete-btn">Delete</button>
            </div>
        )}
        {match.status === 'completed' && (
            <p className='match-result'>
                üèÜ Winner: {match.winner} <br />
                Score: {match.home_team} = {match.scoreA} ---- {match.away_team} = {match.scoreB}
            </p>
        )}
    </div>
)) : <p>No scheduled matches available.</p>}


<h2>Playoffs</h2>
<button 
    className="playoff-btn" 
    onClick={() => navigate(`/playoffs/${id}`)}
    disabled={!allMatchesCompleted || (playoffMatches.length > 0 && playoffMatches.some(match => match.status !== "completed"))}
>
    Proceed to Playoffs
</button>


<h3>Semifinal-1</h3>
{playoffMatches.length > 0 ? playoffMatches.filter(match=>match.round==="Semifinal-1").map((match, index) => (
    <div key={index} className="match-card">
        <h3>{match.home_team} vs {match.away_team}</h3>
        <p>Round: {match.round}</p>
        <p>Status: {match.status}</p>
        {match.status === 'scheduled' && (
            <div className='buttons'>
                <button className="start-btn" onClick={() => {
    navigate(`/live-match/${match.id}`);
    localStorage.setItem("resetMatch", "true");  // Store reset flag
}}>Start</button>

                <button onClick={() => deleteMatch(match.id)} className="delete-btn">Delete</button>
            </div>
        )}
        {match.status === 'completed' && (
            <p className='match-result'>
                üèÜ Winner: {match.winner} <br />
                Score: {match.home_team} = {match.scoreA} - {match.away_team} = {match.scoreB}
            </p>
        )}
    </div>
)) : <p>No semifinal-1 matches scheduled yet.</p>}
<h3>Semifinal-2</h3>
{playoffMatches.length > 0 ? playoffMatches.filter(match=>match.round==="Semifinak-2").map((match, index) => (
    <div key={index} className="match-card">
        <h3>{match.home_team} vs {match.away_team}</h3>
        <p>Round: {match.round}</p>
        <p>Status: {match.status}</p>
        {match.status === 'scheduled' && (
            <div className='buttons'>
                <button className="start-btn" onClick={() => {
    navigate(`/live-match/${match.id}`);
    localStorage.setItem("resetMatch", "true");  // Store reset flag
}}>Start</button>

                <button onClick={() => deleteMatch(match.id)} className="delete-btn">Delete</button>
            </div>
        )}
        {match.status === 'completed' && (
            <p className='match-result'>
                üèÜ Winner: {match.winner} <br />
                Score: {match.home_team} = {match.scoreA} - {match.away_team} = {match.scoreB}
            </p>
        )}
    </div>
)) : <p>No semifinal-2 matches scheduled yet.</p>}
            <h3>Points Table</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Team</th>
                        <th>Played</th>
                        <th>Wins</th>
                        <th>Losses</th>
                        <th>Points</th>
                    </tr>
                </thead>
                <tbody>
    {pointsTable.length > 0 ? (
        pointsTable.map((team, index) => (
            <tr key={index}>
                <td>{team.team_name}</td>
                <td>{team.games_played}</td>
                <td>{team.wins}</td>
                <td>{team.losses}</td>
                <td>{team.wins}</td>
            </tr>
        ))
    ) : (
        <tr>
            <td colSpan="5">No points data available</td>
        </tr>
    )}
</tbody>

            </table>
        </div>
    );
}

export default ViewTournament;
