import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

const LiveMatch = () => {
  const { matchId } = useParams();
  console.log("üîπ Match ID received:", matchId, "Type:", typeof matchId);

  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [scoreA, setScoreA] = useState(0);
  const [scoreB, setScoreB] = useState(0);
  const [timer, setTimer] = useState(0);
  const [running, setRunning] = useState(false);
  const [timeoutsA, setTimeoutsA] = useState(0);
  const [timeoutsB, setTimeoutsB] = useState(0);

  useEffect(() => {
    const fetchMatch = async () => {
      try {
        console.log(`üì¢ Fetching match data for ID: ${matchId}`);
        const response = await fetch(`http://localhost:5000/api/matches/${matchId}`);

        if (!response.ok) {
          throw new Error(`‚ùå API request failed with status ${response.status}`);
        }

        const data = await response.json();
        console.log("‚úÖ Match data received:", data);

        if (!data || !data.home_team || !data.away_team) {
          throw new Error("‚ùå Match data is incomplete");
        }

        setMatch(data);
        setScoreA(data.scoreA || 0);
        setScoreB(data.scoreB || 0);
        setTimeoutsA(data.timeoutsA || 0);
        setTimeoutsB(data.timeoutsB || 0);
        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching match:", err);
        setLoading(false);
      }
    };

    fetchMatch();
  }, [matchId]);

  useEffect(() => {
    socket.on("scoreUpdated", (data) => {
      if (data.matchId === matchId) {
        setScoreA(data.scoreA);
        setScoreB(data.scoreB);
      }
    });

    return () => socket.off("scoreUpdated");
  }, [matchId]);

  const updateScore = async (team) => {
    const newScoreA = team === "A" ? scoreA + 3 : scoreA;
    const newScoreB = team === "B" ? scoreB + 3 : scoreB;
  
    try {
      const response = await fetch(`http://localhost:5000/api/matches/${matchId}/score`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scoreA: newScoreA, scoreB: newScoreB }),
      });
  
      if (response.ok) {
        setScoreA(newScoreA);
        setScoreB(newScoreB);
        socket.emit("updateScore", { matchId, scoreA: newScoreA, scoreB: newScoreB });
      } else {
        console.error("Error updating score:", await response.json());
      }
    } catch (err) {
      console.error("Error sending score update:", err);
    }
  };
  
  useEffect(() => {
    let interval;
    if (running && timer > 0) {
      interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
    }
    return () => clearInterval(interval);
  }, [running, timer]);

  const startTimer = (quarterTime) => {
    setTimer(quarterTime * 60);
    setRunning(true);
  };

  const resetTimer = (quarterTime) => {
    setTimer(quarterTime * 60);
    setRunning(false);
  };

  if (loading) return <p>Loading match data...</p>;
  if (!match) return <p>‚ùå No match found!</p>;

  return (
    <div>
      <h1>{match.home_team} vs {match.away_team}</h1>
      <p>Score: {scoreA} - {scoreB}</p>
      <button onClick={() => updateScore("A")}>Add 3 to {match.home_team}</button>
      <button onClick={() => updateScore("B")}>Add 3 to {match.away_team}</button>

      <div className="timer">
        <p>Timer: {Math.floor(timer / 60)}:{timer % 60 < 10 ? `0${timer % 60}` : timer % 60}</p>
        <button onClick={() => startTimer(12)}>Start Timer</button>
        <button onClick={() => resetTimer(12)}>Reset Timer</button>
      </div>
    </div>
  );
};

export default LiveMatch;
