import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
//import "../styles/Playoffs.css";

function Playoffs() {
    const { tournamentId } = useParams();
    const navigate = useNavigate();
    const [topTeams, setTopTeams] = useState([]);
    const [matches, setMatches] = useState([]);
    const [playoffStage, setPlayoffStage] = useState(0);
    const [homeTeam, setHomeTeam] = useState("");
    const [awayTeam, setAwayTeam] = useState("");
    const [matchDate, setMatchDate] = useState("");
    const [matchTime, setMatchTime] = useState("");

    useEffect(() => {
        async function fetchData() {
            await fetchTopTeams();
            await fetchMatches();
            await fetchPlayoffStage();
        }
        fetchData();
    }, [tournamentId]);

    const fetchTopTeams = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/points-table`);
            if (!response.ok) throw new Error("Failed to fetch points table");
    
            const data = await response.json();
            console.log("üéØ Full Points Table Fetched:", data);
    
            if (Array.isArray(data) && data.length > 0) {
                // Determine the number of teams to display based on the playoff stage
                let topCount = 4;
                const playoffMatchesResponse = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/matches`);
            const matches = await playoffMatchesResponse.json();

            const semiFinal1 = matches.find(match => match.round === "Semifinal-1" && match.status === "completed");
            const semiFinal2 = matches.find(match => match.round === "Semifinal-2" && match.status === "completed");

            if (semiFinal1) topCount = 3; // After Semifinal-1, show top 3 teams
            if (semiFinal2) topCount = 2;
                // Sort the teams by wins, then by point difference
                const sortedData = data
                    .sort((a, b) => 
                        b.wins - a.wins ||  // Sort by wins first
                        b.point_difference - a.point_difference  // If wins are equal, sort by point difference
                    )
                    .slice(0, topCount)  // Select the required number of teams
                    .map((team, index) => ({ 
                        rank: index + 1, 
                        name:team.name||team.team_name, 
                        points: team.wins // Ensure 'points' field is correctly assigned
                    }));
    
                console.log(`üèÜ Top ${topCount} Teams:`, sortedData);
                setTopTeams(sortedData);
            } else {
                console.log("‚ùå No teams available for ranking.");
                setTopTeams([]);
            }
        } catch (error) {
            console.error("‚ùå Error fetching points table:", error);
        }
    };
    
    
    
    

    const fetchMatches = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/matches`);
            const data = await response.json();
            const playoffMatches = data.filter(match => match.round.includes('Semifinal') || match.round.includes('Final'));
    
            setMatches(playoffMatches); // ‚úÖ Store the fetched playoff matches
    
            // ‚úÖ Dynamically update `topTeams` based on match completion
            fetchTopTeams();
        } catch (error) {
            console.error("Error fetching playoff matches:", error);
        }
    };
    
    
    

    const fetchPlayoffStage = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/playoff-stage`);
            const data = await response.json();
            setPlayoffStage(data.stage);
        } catch (error) {
            console.error("Error fetching playoff stage:", error);
        }
    };
    
    const updatePlayoffStage = async (newStage) => {
        try {
            await fetch(`http://localhost:5000/api/tournament/${tournamentId}/update-playoff-stage`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ stage: newStage })
            });
            window.location.reload();
        } catch (error) {
            console.error("Error updating playoff stage:", error);
        }
    };
    
    const handleScheduleMatch = async () => {
        if (!homeTeam || !awayTeam || homeTeam === awayTeam) {
            alert("Please select two different teams.");
            return;
        }
        if (!matchDate || !matchTime) {
            alert("Please select a valid date and time.");
            return;
        }
    
        let roundName = playoffStage === 0 ? "Semifinal-1" : playoffStage === 1 ? "Semifinal-2" : "Final";
    
        try {
            const response = await fetch(`http://localhost:5000/api/tournament/${tournamentId}/schedule-manual-playoff`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    home_team: homeTeam,
                    away_team: awayTeam,
                    round: roundName,
                    match_datetime: `${matchDate}T${matchTime}`
                })
            });
    
            if (response.ok) {
                alert("Match scheduled successfully!");
            } else {
                alert("Failed to schedule match.");
            }
        } catch (error) {
            console.error("Error scheduling match:", error);
        }
    };
    
    return (
        <div className="container">
            <h2>Playoffs</h2>

            <h3>
    {topTeams.length === 4 
        ? "üî• Top 4 Playoff Teams" 
        : topTeams.length === 3 
        ? "üèÜ Final Standings - Top 3 Teams" 
        : "üèÜ Final Match - Top 2 Teams"}
</h3>


<ul>
    {topTeams.length > 0 ? (
        topTeams.map((team, index) => (
            <li key={team.rank}>
                {team.rank}. {team.name?team.name:"missing"} - {team.points} Points
            </li>
        ))
    ) : (
        <li>No teams available</li>
    )}
</ul>




            <h3>Schedule a Playoff Match</h3>
            <select value={homeTeam} onChange={(e) => setHomeTeam(e.target.value)}>
                <option value="">Select Home Team</option>
                {topTeams.map(team => (
                    <option key={team.name} value={team.name}>{team.name}</option>
                ))}
            </select>
            <select value={awayTeam} onChange={(e) => setAwayTeam(e.target.value)}>
                <option value="">Select Away Team</option>
                {topTeams.map(team => (
                    <option key={team.name} value={team.name}>{team.name}</option>
                ))}
            </select>
            <input type="date" value={matchDate} onChange={(e) => setMatchDate(e.target.value)} />
            <input type="time" value={matchTime} onChange={(e) => setMatchTime(e.target.value)} />
            <button onClick={handleScheduleMatch}>Schedule Match</button>

            <h3>Playoff Matches</h3>
            {matches.map(match => (
                <div key={match.id} className="match-card">
                    <h3>{match.home_team} vs {match.away_team}</h3>
                    <p>Round: {match.round}</p>
                    <p>Date: {new Date(match.time).toLocaleString()}</p>
                    <p>Status: {match.status}</p>
                    <button onClick={() => navigate(`/live-match/${match.id}`)}>Start Match</button>
                </div>
            ))}

            <button onClick={() => navigate(-1)}>Back</button>
        </div>
    );
}

export default Playoffs;
