import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [tournament, setTournament] = useState(null);
  const [matches, setMatches] = useState([]);
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    async function fetchData() {
      await fetchTournamentDetails();
      await fetchMatches();
      await fetchTeams();
    }
    fetchData();
  }, [id]);

  const fetchTournamentDetails = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/tournament/${id}`);
      const data = await res.json();
      if (data && data.id) {
        setTournament(data);
      } else {
        console.error("Tournament not found.");
      }
    } catch (error) {
      console.error('Error fetching tournament details:', error);
    }
  };

  const fetchMatches = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
      const data = await res.json();
      if (Array.isArray(data)) {
        setMatches(data);
      } else {
        console.error("Unexpected API response format:", data);
      }
    } catch (error) {
      console.error('Error fetching matches:', error);
    }
  };

  const fetchTeams = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/tournament/${id}/teams`);
      const data = await res.json();
      if (Array.isArray(data) && data.length > 0) {
        setTeams(data);
      } else {
        console.error("Unexpected API response format or no teams available:", data);
      }
    } catch (error) {
      console.error('Error fetching teams:', error);
    }
  };

  const handleDeleteMatch = async (matchId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/matches/${matchId}`, { method: 'DELETE' });
      if (res.ok) {
        setMatches(matches.filter(match => match.id !== matchId));
        alert("Match deleted successfully!"); 
      } else {
        console.error("Failed to delete match");
      }
    } catch (error) {
      console.error('Error deleting match:', error);
    }
  };

  const handleStartMatch = async (matchId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/matches/${matchId}/start`, { method: 'POST' });
      if (res.ok) {
        alert("Match started successfully!"); 
        navigate(`/live-match/${matchId}`); // ✅ Navigate to live match page
      } else {
        console.error("Failed to start match");
      }
    } catch (error) {
      console.error('Error starting match:', error);
    }
  };

  if (!tournament) return <p>Loading tournament details...</p>;

  return (
    <div className="container1">
      <nav className="navbar">
          <img src="/logo.png" alt="Logo" className='tour-logo'/>
        <ul className="nav-links">
          <li>Home</li>
          <li>Game</li>
          <li>Tournaments</li>
          <li>About Us</li>
          <li>Contact</li>
          <li>Account</li>
        </ul>
      </nav>
      <div className='tournament'>
      <h2>{tournament.name} ({tournament.type})</h2>
      <div className='spacer'></div>
      <div className='teams'>
      <p className='section-title'>Teams: {teams.length}</p>
      {teams.length > 0 ? (
        <div>
          {teams.map((team) => (
            <span key={team.id} className='team-tag'>
              {team.teamName}
            </span>
          ))}
          </div>
      ) : (
        <p>No teams available.</p>
      )}
      </div>

      <p className='section-title'>Matches</p>
      <div className='matches'>
      {matches.length === 0 ? (
        <p>No matches scheduled yet.</p>
      ) : (<div>
          {matches.map((match, index) => (
            <div key={index} className="match-card">
              <h3>{match.home_team} vs {match.away_team}</h3>
              <p>Date: {new Date(match.time).toLocaleString()}</p>
              <div className='buttons'>
              <button onClick={() => handleStartMatch(match.id)} className="start-btn">Start</button>
              <button onClick={() => handleDeleteMatch(match.id)} className="delete-btn">Delete</button>
              </div>
            </div>
          ))}
          </div>
      )}
    </div>
    <button onClick={() => navigate(-1)} className="back-btn">Back</button>
    </div>
    </div>
  );
}

export default ViewTournament;
