import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";
import "../styles/LiveMatch.css";

const LiveMatch = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [scoreA, setScoreA] = useState(0);
  const [scoreB, setScoreB] = useState(0);
  const [timeoutsA, setTimeoutsA] = useState(7);
  const [timeoutsB, setTimeoutsB] = useState(7);
  const [mainTimer, setMainTimer] = useState(600);
  const [secondTimer, setSecondTimer] = useState(120);
  const [isMainRunning, setIsMainRunning] = useState(false);
  const [isSecondRunning, setIsSecondRunning] = useState(false);
  const [players, setPlayers] = useState([]);
  const [teams,setTeams]=useState([]);
  const [fouls, setFouls] = useState({});
  const socket = io("http://localhost:5000", {
    transports: ["websocket", "polling"],
    reconnection: true,
    reconnectionAttempts: 5,
    reconnectionDelay: 2000
  });
  const getTeamById = (teamId) => {
    const team = teams.find(team => team.id === teamId);
    if (!team) {
      console.warn(`⚠️ Team not found for ID: ${teamId}`);
      return "Unknown Team";
    }
    return team.teamName;
  };
  
  
  
  useEffect(() => {
    const fetchMatch = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/matches/${matchId}`);
        
        if (!response.ok) {
          console.error(`❌ API Error: ${response.status}`);
          const errorText = await response.text();
          throw new Error(`API request failed: ${errorText}`);
        }
    
        const data = await response.json();
        console.log("✅ Fetched match data:", data);
    
        if (!data.match) {
          console.warn("⚠️ No match data received");
          throw new Error("Match not found");
        }
    
        setMatch(data.match);
        setScoreA(data.match.scoreA || 0);
        setScoreB(data.match.scoreB || 0);
        setTimeoutsA(data.match.timeoutsA ?? 7);
        setTimeoutsB(data.match.timeoutsB ?? 7);
        setPlayers(data.players || []);
        setFouls(data.players.reduce((acc, player) => ({ ...acc, [player.id]: player.fouls || 0 }), {}));
      } catch (err) {
        console.error("❌ Error fetching match:", err);
        setMatch(null);
      } finally {
        setLoading(false);
      }
    };
    const fetchTeams = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/tournament/${match?.tournament_id}/teams`);

        if (!response.ok) throw new Error("Failed to fetch teams");
        const data = await response.json();
        setTeams(data);  // ✅ Ensure teams are set correctly
      } catch (err) {
        console.error("Error fetching teams:", err);
      }
    };
    fetchMatch();
    fetchTeams();
    socket.on("connect", () => {
      console.log("✅ WebSocket Connected!");
    });

    socket.on("disconnect", () => {
      console.warn("❌ WebSocket Disconnected!");
    });

    // Listen for match updates
    socket.on("updateMatch", (updatedMatch) => {
      console.log("🔄 Match Updated:", updatedMatch);
      setMatch(updatedMatch);
    });

    // Listen for player updates
    socket.on("updatePlayers", (updatedPlayers) => {
      console.log("🔄 Players Updated:", updatedPlayers);
      setPlayers(updatedPlayers);
    });

    return () => {
      socket.off("connect");
      socket.off("disconnect");
      socket.disconnect();
    };
  }, [matchId]);

  const updatePlayerScore = async (playerId, points) => {
    try {
      const response = await fetch(`http://localhost:5000/api/players/${playerId}/score`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ points }),
      });
  
      if (response.ok) {
        const updatedPlayer = players.find(player => player.id === playerId);
        if (!updatedPlayer) return;
  
        // Get the player's team name from the 'teams' state
        const playerTeam = teams.find(team => team.id === updatedPlayer.teamId)?.teamName;
        if (!playerTeam) return;
  
        // Compare against match.home_team and match.away_team
        const isHomeTeam = playerTeam === match.home_team;
  
        setPlayers(players.map(player => 
          player.id === playerId ? { ...player, score: player.score + points } : player
        ));
  
        if (isHomeTeam) {
          setScoreA(prevScore => prevScore + points);
        } else {
          setScoreB(prevScore => prevScore + points);
        }
        
        socket.emit("updatePlayerScore", { playerId, points });
      }
    } catch (err) {
      console.error("Error updating player score:", err);
    }
  };
  

  const handleFoul = async (playerId) => {
    try {
      const player = players.find(player => player.id === playerId);
      if (!player) {
        console.error("❌ Player not found!");
        return;
      }
  
      // Get the player's correct team name
      const playerTeamName = teams.find(team => team.id === player.teamId)?.teamName;
      if (!playerTeamName) {
        console.error("❌ Team not found for player!");
        return;
      }
  
      // Check if the player is in the home or away team
      const isHomeTeam = match.home_team === playerTeamName;
  
      console.log(`✅ Updating foul for Player: ${playerId}, Team: ${playerTeamName}, Home Team: ${match.home_team}, Away Team: ${match.away_team}`);
  
      // Prevent unnecessary state updates
      if (fouls[playerId] === undefined) return;
  
      // Send request to update fouls in the backend
      const response = await fetch(`http://localhost:5000/api/players/${playerId}/foul`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
      });
  
      if (response.ok) {
        const data = await response.json();
        console.log("✅ Foul updated successfully for:", data.player.playerName);
  
        // Update fouls state **only for the affected player**
        setFouls(prevFouls => ({
          ...prevFouls,
          [playerId]: data.player.fouls, // Ensure only this player's fouls are updated
        }));
  
        // Emit WebSocket event for **only this player**
        socket.emit("updateFoul", {
          playerId,
          team: isHomeTeam ? "home" : "away",
          fouls: data.player.fouls,
        });
      } else {
        console.error("❌ Failed to update foul:", await response.text());
      }
    } catch (err) {
      console.error("❌ Error updating foul:", err);
    }
  };
  
  
  
  useEffect(() => {
    let interval;
    if (isMainRunning && mainTimer > 0) {
      interval = setInterval(() => setMainTimer((prev) => prev - 1), 1000);
    }
    return () => clearInterval(interval);
  }, [isMainRunning, mainTimer]);

  useEffect(() => {
    let interval;
    if (isSecondRunning && secondTimer > 0) {
      interval = setInterval(() => setSecondTimer((prev) => prev - 1), 1000);
    }
    return () => clearInterval(interval);
  }, [isSecondRunning, secondTimer]);

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${String(min).padStart(2, "0")}:${String(sec).padStart(2, "0")}`;
  };

  if (loading) return <p>Loading match data...</p>;
  if (!match) return <p>No match found!</p>;

  return (
    <div className="scoreboard-container">
      <nav className="navbar">
        <div className="logo">
          <span className="logo-box">24</span>
          <span className="logo-text">Score It</span>
        </div>
        <ul>
          <li>Home</li>
          <li>Game</li>
          <li>Tournaments</li>
          <li>About Us</li>
          <li>Contact</li>
          <li>Account</li>
        </ul>
      </nav>

      <h1 className="match-title">{match.home_team} vs {match.away_team}</h1>
      <h2 className="match-score">Match Score</h2>

      <div className="timer-section">
        <div className="timer-box">
          <div className="time-display">{formatTime(mainTimer)}</div>
          <div className="controls">
            <button onClick={() => setIsMainRunning(true)}>Start</button>
            <button onClick={() => setIsMainRunning(false)}>Stop</button>
            <button onClick={() => setMainTimer(600)}>Reset</button>
          </div>
        </div>
        <h2 className="quarter">3rd Quarter</h2>
        <div className="timer-box">
          <div className="time-display">{formatTime(secondTimer)}</div>
          <div className="controls">
            <button onClick={() => setIsSecondRunning(true)}>Start</button>
            <button onClick={() => setIsSecondRunning(false)}>Stop</button>
            <button onClick={() => setSecondTimer(120)}>Reset</button>
          </div>
        </div>
      </div>

      <div className="team-section">
        {["home_team", "away_team"].map((team, index) => (
          <div key={index} className="team-box">
            <h3>{team === "home_team" ? match.home_team : match.away_team}</h3>
            <div className="score">{team === "home_team" ? scoreA : scoreB}</div>
            <div className="timeouts">
              <p>Timeouts</p>
              <div className="timeout-box">{team === "home_team" ? timeoutsA : timeoutsB}</div>
            </div>
            <div className="fouls">
  <p>Fouls:</p>
  <div className="foul-box">
    {team === "home_team"
      ? players.filter(p => getTeamById(p.teamId) === match.home_team)
               .reduce((sum, p) => sum + (fouls[p.id] || 0), 0)
      : players.filter(p => getTeamById(p.teamId) === match.away_team)
               .reduce((sum, p) => sum + (fouls[p.id] || 0), 0)}
  </div>
</div>
            <h4>Players:</h4>
            <ul className="player-list">
            {players.length > 0 ? (
              players.filter(player => {
                const playerTeamName = getTeamById(player.teamId);
                return (team === "home_team" && match.home_team === playerTeamName) ||
                       (team === "away_team" && match.away_team === playerTeamName);
              })
                .map(player => (
                  <li key={player.id} className="player-row">
                    <span className="player-name">{player.playerName}</span>
                    <span className="score-box">{player.score}</span>
                    <div className="player-buttons">
                      <button onClick={() => updatePlayerScore(player.id, 3, team)}>+3</button>
                      <button onClick={() => updatePlayerScore(player.id, 2, team)}>+2</button>
                      <button onClick={() => updatePlayerScore(player.id, 1, team)}>+1</button>
                      <button onClick={() => handleFoul(player.id, team)} className="btn red">F</button>
                    </div>
                  </li>
                ))
            ) : (
              <p>No players found for this team.</p>
            )}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LiveMatch;
