import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";

const Match = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [scoreA, setScoreA] = useState(0);
  const [scoreB, setScoreB] = useState(0);
  const [players, setPlayers] = useState([]);
  const [fouls, setFouls] = useState({});
  const [mainTimer, setMainTimer] = useState(600);
  const [secondTimer, setSecondTimer] = useState(120);
  
  const socket = io("http://localhost:5000", {
    transports: ["websocket", "polling"],
    reconnection: true,
    reconnectionAttempts: 10,
    reconnectionDelay: 2000,
  });

  useEffect(() => {
    const fetchMatch = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/matches/${matchId}`);
        if (!response.ok) throw new Error("Failed to fetch match data");

        const data = await response.json();
        if (!data.match) throw new Error("Match not found");

        setMatch(data.match);
        setScoreA(data.match.scoreA || 0);
        setScoreB(data.match.scoreB || 0);
        setPlayers(Array.isArray(data.players) ? data.players : []);
        setFouls(
          (data.players || []).reduce((acc, player) => ({ ...acc, [player.id]: player.fouls || 0 }), {})
        );
      } catch (err) {
        console.error("Error fetching match:", err);
        setMatch(null);
        setPlayers([]);
      } finally {
        setLoading(false);
      }
    };

    fetchMatch();

    socket.on("updateMatch", (updatedMatch) => {
      setMatch(updatedMatch);
      setScoreA(updatedMatch.scoreA || 0);
      setScoreB(updatedMatch.scoreB || 0);
    });

    socket.on("updatePlayers", (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socket.on("playerFoulUpdated", (data) => {
      setFouls((prevFouls) => ({ ...prevFouls, [data.playerId]: data.fouls }));
    });

    socket.on("updateMainTimer", (newTime) => setMainTimer(newTime));
    socket.on("updateSecondTimer", (newTime) => setSecondTimer(newTime));

    return () => {
      socket.off("updateMatch");
      socket.off("updatePlayers");
      socket.off("playerFoulUpdated");
      socket.off("updateMainTimer");
      socket.off("updateSecondTimer");
      socket.disconnect();
    };
  }, [matchId]);

  if (loading) return <p>Loading match data...</p>;
  if (!match) return <p>No match found!</p>;

  return (
    <div className="scoreboard-container">
      <h1 className="match-title">{match.home_team} vs {match.away_team}</h1>
      <h2 className="match-score">Match Score</h2>

      <div className="timer-section">
        <div className="timer-box">
          <p>Main Timer</p>
          <div className="time-display">{mainTimer}</div>
        </div>
        <div className="timer-box">
          <p>Secondary Timer</p>
          <div className="time-display">{secondTimer}</div>
        </div>
      </div>

      <div className="team-section">
        {[["home_team", scoreA], ["away_team", scoreB]].map(([teamType, score], index) => (
          <div key={index} className="team-box">
            <h3>{teamType === "home_team" ? match.home_team : match.away_team}</h3>
            <div className="score">{score}</div>
            <h4>Players:</h4>
            <ul className="player-list">
              {(players || []).filter(player => player.teamName === (teamType === "home_team" ? match.home_team : match.away_team))
                .map(player => (
                  <li key={player.id} className="player-row">
                    <span className="player-name">{player.playerName}</span>
                    <span className="score-box">{player.score}</span>
                    <span className="foul-count">Fouls: {fouls[player.id] || 0}</span>
                  </li>
                ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Match;
