import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import "../styles/ViewTournament.css";

function ViewTournament() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [tournament, setTournament] = useState(null);
    const [matches, setMatches] = useState([]);
    const [playoffMatches, setPlayoffMatches] = useState([]);
    const [pointsTable, setPointsTable] = useState([]);
    const [allMatchesCompleted, setAllMatchesCompleted] = useState(false);
    const [playoffButtonDisabled, setPlayoffButtonDisabled] = useState(true);
    const [playoffStage, setPlayoffStage] = useState(1);

    useEffect(() => {
        async function fetchData() {
            await fetchTournamentDetails();
            await fetchMatches();
            await fetchPointsTable();
        }
        fetchData();
    }, [id]);

    useEffect(() => {
        if (matches.length > 0) {
            const normalMatches = matches.filter(match => !match.round.includes("Semifinal") && !match.round.includes("Final"));
            const allCompleted = normalMatches.every(match => match.status === "completed");

            setAllMatchesCompleted(allCompleted);
            setPlayoffButtonDisabled(!allCompleted);
        }
    }, [matches]);

    const fetchTournamentDetails = async () => {
        try {
            const res = await fetch(`http://localhost:5000/tournament/${id}`);
            const data = await res.json();
            if (data?.id) {
                setTournament(data);
            }
        } catch (error) {
            console.error('Error fetching tournament details:', error);
        }
    };

    const fetchMatches = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/matches`);
            const data = await res.json();
            if (!Array.isArray(data)) return;
            
            setMatches(data.filter(match => !match.round.includes("Semifinal") && !match.round.includes("Final")));
            setPlayoffMatches(data.filter(match => match.round.includes("Semifinal") || match.round.includes("Final")));
        } catch (error) {
            console.error('Error fetching matches:', error);
        }
    };

    const fetchPointsTable = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/tournament/${id}/points-table`);
            const data = await res.json();
            if (!Array.isArray(data)) return;

            setPointsTable(data.map(team => ({
                ...team,
                points: team.wins, // Ensure points are correct
            })));
        } catch (error) {
            console.error('Error fetching points table:', error);
        }
    };

    const handleProceedToPlayoffs = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/tournament/${id}/top-teams`);
            const topTeams = await response.json();

            if (playoffStage === 1 && topTeams.length >= 4) {
                await scheduleMatch(topTeams[0].team_name, topTeams[3].team_name, "Semifinals 1");
                await scheduleMatch(topTeams[1].team_name, topTeams[2].team_name, "Semifinals 1");
                setPlayoffStage(2);
            } else if (playoffStage === 2 && topTeams.length >= 3) {
                await scheduleMatch(topTeams[0].team_name, topTeams[2].team_name, "Semifinals 2");
                setPlayoffStage(3);
            } else if (playoffStage === 3 && topTeams.length >= 2) {
                await scheduleMatch(topTeams[0].team_name, topTeams[1].team_name, "Final");
                setPlayoffStage(4);
            }
            setPlayoffButtonDisabled(true);
        } catch (error) {
            console.error("Error proceeding to playoffs:", error);
        }
    };

    const scheduleMatch = async (home, away, round) => {
        await fetch(`http://localhost:5000/api/tournament/${id}/schedule-manual-playoff`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                home_team: home,
                away_team: away,
                round: round,
                match_datetime: new Date().toISOString()
            })
        });
    };

    return (
        <div className="container1">
            <nav className="navbar">
                <img src="/logo.png" alt="Logo" className='tour-logo'/>
                <ul className="nav-links">
                    <li>Home</li>
                    <li>Game</li>
                    <li>Tournaments</li>
                    <li>About Us</li>
                    <li>Contact</li>
                    <li>Account</li>
                </ul>
            </nav>

            <div className='tournament'>
                <h2>{tournament?.name} ({tournament?.type})</h2>

                <p className='section-title'>Scheduled Matches</p>
                {matches.length === 0 ? <p>No matches scheduled yet.</p> : (
                    <div>
                        {matches.map((match, index) => (
                            <div key={index} className="match-card">
                                <h3>{match.home_team} vs {match.away_team}</h3>
                                <p>Date: {new Date(match.time).toLocaleString()}</p>
                                <p>Status: {match.status}</p>
                                {match.status === 'completed' && match.winner ? (
    <p className='match-result'>üèÜ Winner: {match.winner}</p>
) : (
    match.status === 'completed' && <p className='match-result'>Winner not recorded</p>
)}

                            </div>
                        ))}
                    </div>
                )}

                <h2>Playoffs</h2>
                {playoffMatches.length > 0 ? (
                    <div>
                        {playoffMatches.map((match, index) => (
                            <div key={index} className="match-card">
                                <h3>{match.home_team} vs {match.away_team}</h3>
                                <p>Round: {match.round}</p>
                                <p>Date: {new Date(match.time).toLocaleString()}</p>
                                <p>Status: {match.status}</p>
                                {match.status === 'completed' && <p className='match-result'>üèÜ Winner: {match.winner}</p>}
                            </div>
                        ))}
                    </div>
                ) : <p>No playoff matches scheduled yet.</p>}

                <button 
                    className="playoff-btn" 
                    onClick={handleProceedToPlayoffs}
                    disabled={playoffButtonDisabled}
                >
                    {playoffStage === 1 ? "Proceed to Semifinals-1" : 
                     playoffStage === 2 ? "Proceed to Semifinals-2" : 
                     playoffStage === 3 ? "Proceed to Finals" : "Tournament Completed"}
                </button>
                <h2>Points Table</h2>
                <table border="1">
                    <thead>
                        <tr>
                            <th>Team</th>
                            <th>Played</th>
                            <th>Wins</th>
                            <th>Losses</th>
                            <th>Points</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pointsTable.length > 0 ? (
                            pointsTable.map((team, index) => (
                                <tr key={index}>
                                    <td>{team.team_name}</td>
                                    <td>{team.games_played}</td>
                                    <td>{team.wins}</td>
                                    <td>{team.losses}</td>
                                    <td>{team.points}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="5">No points data available</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ViewTournament;
