import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function ScheduleMatch() {
  const { tournamentId } = useParams();
  const [matchFormat, setMatchFormat] = useState("knockout");
  const [rounds, setRounds] = useState(1);
  const [matchDate, setMatchDate] = useState("");
  const [matchTime, setMatchTime] = useState("10:00");
  const [matches, setMatches] = useState([]);

  const handleSchedule = async () => {
    if (!matchDate) {
      alert("Please select a match start date.");
      return;
    }
  
    let endpoint = matchFormat === "round-robin"
      ? `/api/tournaments/${tournamentId}/schedule-roundrobin`
      : `/api/tournaments/${tournamentId}/schedule-knockout`;
  
    let body = JSON.stringify(
      matchFormat === "round-robin"
        ? { rounds, matchDate, matchTime }
        : { matchDate, matchTime }
    );
  
    try {
      console.log("üì¢ Sending request to:", `http://localhost:5000${endpoint}`);
      console.log("üì¶ Request body:", body);
  
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: body,
      });
  
      if (!response.ok) {
        const errorText = await response.text(); // Read error message
        console.error("‚ùå Server Error:", errorText);
        alert(`Error: ${errorText}`);
        return;
      }
  
      const data = await response.json();
      console.log("‚úÖ Response from server:", data);
  
      setMatches(data.matches);
      alert("Schedule created successfully!");
    } catch (error) {
      console.error("üö® Fetch Error:", error);
      alert("An unexpected error occurred.");
    }
  };

  return (
    <div>
      <h2>Schedule Matches</h2>
      <label>
        Match Format:
        <select value={matchFormat} onChange={(e) => setMatchFormat(e.target.value)}>
          <option value="knockout">Knockout</option>
          <option value="round-robin">Round Robin</option>
        </select>
      </label>
      {matchFormat === "round-robin" && (
        <label>
          Rounds:
          <input type="number" value={rounds} min="1" onChange={(e) => setRounds(Number(e.target.value))} />
        </label>
      )}
      <label>
        Match Start Date:
        <input type="date" value={matchDate} onChange={(e) => setMatchDate(e.target.value)} />
      </label>
      <label>
        Match Start Time:
        <input type="time" value={matchTime} onChange={(e) => setMatchTime(e.target.value)} />
      </label>
      <button onClick={handleSchedule}>Generate Schedule</button>
    </div>
  );
}

export default ScheduleMatch;
