import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
//import "../styles/ViewTournament.css";

function ViewTournament() {
  const { id } = useParams(); // ✅ Get tournament ID from URL
  const navigate = useNavigate();
  const [tournament, setTournament] = useState(null);
  const [matches, setMatches] = useState([]);
  const [teams, setTeams] = useState([]);

  // ✅ Fetch tournament details & matches when page loads
  useEffect(() => {
    async function fetchData() {
      await fetchTournamentDetails();
      await fetchMatches();
      await fetchTeams();
    }
    fetchData();
  }, []);
  
  // ✅ Fetch Tournament Details
  const fetchTournamentDetails = () => {
    fetch(`http://localhost:5000/tournament/${id}`)
      .then((res) => res.json())
      .then((data) => {
        if (data && data.id) {
          setTournament(data);
        } else {
          console.error("Tournament not found.");
        }
      })
      .catch((error) => console.error('Error fetching tournament details:', error));
  };

  // ✅ Fetch Matches for the Tournament
  const fetchMatches = () => {
    fetch(`http://localhost:5000/api/tournament/${id}/matches`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setMatches(data);
        } else {
          console.error("Unexpected API response format:", data);
        }
      })
      .catch((error) => console.error('Error fetching matches:', error));
  };

  // ✅ Fetch Teams for the Tournament
  const fetchTeams = () => {
    fetch(`http://localhost:5000/api/tournament/${id}/teams`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setTeams(data);
        } else {
          console.error("Unexpected API response format:", data);
        }
      })
      .catch((error) => console.error('Error fetching teams:', error));
  };

  if (!tournament) return <p>Loading tournament details...</p>;

  return (
    <div className="view-tournament">
      <h2>{tournament.name} ({tournament.type})</h2>
      <p><strong>Teams:</strong> {teams.length}</p>
      <ul>
        {teams.map((team) => (
          <li key={team.id}>{team.teamName}</li>
        ))}
      </ul>
      
      <h4>Matches</h4>
      {matches.length === 0 ? (
        <p>No matches scheduled yet.</p>
      ) : (
        <div className="matches-container">
          {matches.map((match) => (
            <div key={match.id} className="match-card">
              <p><strong>{match.home_team} vs {match.away_team}</strong></p>
              <p>Date: {new Date(match.time).toLocaleString()}</p>
            </div>
          ))}
        </div>
      )}

      <button onClick={() => navigate(-1)} className="back-button">Back</button>
    </div>
  );
}

export default ViewTournament;
