import React, { useState } from 'react'; 
import { useParams,useNavigate} from 'react-router-dom';
import "../styles/CreateTeams.css";

function CreateTeams() {
  const { tournamentId } = useParams();
  const navigate=useNavigate();
  const [teamName, setTeamName] = useState('');
  const [players, setPlayers] = useState(['']);

  const handlePlayerChange = (index, value) => {
    const newPlayers = [...players];
    newPlayers[index] = value;
    setPlayers(newPlayers);
  };

  const addPlayerField = () => {
    setPlayers([...players, '']);
  };

  const handleTeamSubmit = async () => {
    console.log('Tournament ID:', tournamentId);
    console.log('Registering team with data:', { teamName, players });
  
    if (!tournamentId || !teamName || players.some(player => !player)) {
      alert('⚠️ Tournament ID, Team Name, or Player Names are missing!');
      return;
    }
  
    try {
      // ✅ Step 1: Fetch current team count (Handle empty case properly)
      const teamCountResponse = await fetch(`http://localhost:5000/api/teams/${tournamentId}`);
      const teamData = await teamCountResponse.json();
  
      if (!teamCountResponse.ok) {
        console.error(`Error fetching team count: ${teamData.error}`);
        alert(`Error fetching team count: ${teamData.error}`);
        return;
      }
  
      // ✅ Step 2: Ensure `teamData` is always an array
      const registeredTeams = Array.isArray(teamData) ? teamData.length : 0;
  
      // ✅ Step 3: Fetch the maximum number of teams allowed
      const tournamentResponse = await fetch(`http://localhost:5000/tournament/${tournamentId}`);
      const tournamentData = await tournamentResponse.json();
  
      if (!tournamentResponse.ok) {
        alert(`Error fetching tournament details: ${tournamentData.error}`);
        return;
      }
  
      // ✅ Step 4: Block registration if the limit is reached
      if (registeredTeams >= tournamentData.number_of_teams) {
        alert('❌ Maximum number of teams reached! Cannot register more teams.');
        return;
      }
  
      // ✅ Step 5: Proceed with team registration
      const response = await fetch('http://localhost:5000/create-teams', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tournamentId, teamName, players }),
      });
  
      const data = await response.json();
      
      if (response.ok) {
        console.log('✅ Response from server:', data);
        alert('✅ Team and players registered successfully!'); // ✅ Show success message
        setTeamName('');
        setPlayers(['']);
      } else {
        console.error('❌ Error from server:', data.error);
        alert(`❌ ${data.error}`); // ✅ Show max teams reached alert
      }
    } catch (error) {
      console.error('❌ Fetch Error:', error);
      alert('❌ An unexpected error occurred while registering the team.');
    }
  };
  
  
  
  
  
  const handleScheduleMatch = () => {
    navigate(`/schedule-match/${tournamentId}`);
  };
  
  

  return (
    <div className='create-teams'>
      <h2>Register Team with Players</h2>
      <input 
        type="text" 
        placeholder="Team Name" 
        value={teamName} 
        onChange={(e) => setTeamName(e.target.value)} 
      />
      <h3>Players</h3>
      {players.map((player, index) => (
        <input
          key={index}
          type="text"
          placeholder={`Player ${index + 1} Name`}
          value={player}
          onChange={(e) => handlePlayerChange(index, e.target.value)}
        />
      ))}
      <button onClick={addPlayerField}>Add Player</button>
      <button onClick={handleTeamSubmit}>Register Team</button>
      <button onClick={handleScheduleMatch}>ScheduleMatch</button>
    </div>
  );
}

export default CreateTeams;
